with open('champ_data.csv','w') as file:
    writer = csv.writer(file, delimiter=',',lineterminator='\n')
    for name in champ_list_edited:
        #Access website
        url = "https://lolalytics.com/aram/worldwide/platinum/plus/champion/" +\
            name
        r = requests.get(url)
        soup = BeautifulSoup(r.text, 'lxml')
        try:
            win_rate = soup.find(class_ = 'lanewin').string
        except:
            print(name)
        #comes in format 46.56%
        win_rate = re.sub('%', '', win_rate)
        win_rate = win_rate[:win_rate.index('.')]
        win_rate = int(win_rate) / 100.
        writer.writerow([champ_list[champ_list_edited.index(name)], win_rate])
